// Convert Mongo Extended JSON docs -> ui_chart batch for Temp & Humidity
// Example doc:
// {
//   payload: {
//     time: { "$numberDouble": "1762381382190.0" },
//     temp: { "$numberInt": "19" },
//     humidity: { "$numberInt": "65" }
//   }
// }

const docs = Array.isArray(msg.payload) ? msg.payload : [];
if (!docs.length) {
    msg.payload = [];
    return msg;
}

// Helper: unwrap Mongo Extended JSON numeric fields
function getNum(v) {
    if (v == null) return NaN;
    if (typeof v === "number") return v;
    if (typeof v === "string") return Number(v);
    if (typeof v === "object") {
        if ("$numberDouble" in v) return Number(v.$numberDouble);
        if ("$numberInt" in v) return Number(v.$numberInt);
        if ("$numberLong" in v) return Number(v.$numberLong);
        if ("$date" in v) return Number(v.$date);
    }
    return NaN;
}

// Build arrays for both temperature and humidity
const tempPoints = [];
const humPoints = [];

for (const d of docs) {
    const t = getNum(d?.payload?.time);
    const temp = getNum(d?.payload?.temp);
    const hum = getNum(d?.payload?.humidity);

    if (!Number.isFinite(t)) continue; // skip invalid times
    const ms = t < 1e12 ? t * 1000 : t; // convert seconds to ms if needed

    if (Number.isFinite(temp)) tempPoints.push({ x: ms, y: temp });
    if (Number.isFinite(hum)) humPoints.push({ x: ms, y: hum });
}

// Sort both by time
tempPoints.sort((a, b) => a.x - b.x);
humPoints.sort((a, b) => a.x - b.x);

// Check if we have any data
if (!tempPoints.length && !humPoints.length) {
    node.warn("No valid data points found in MongoDB results.");
    msg.payload = [];
    return msg;
}

// Format for Node-RED dashboard Chart (Chart.js)
msg.payload = [
    {
        series: ["Temperature (Â°C)", "Humidity (%)"],
        data: [tempPoints, humPoints],
        labels: ["Temp", "Humidity"]
    }
];

return msg;
